import {useState} from 'react'

const SignupComponent=()=>{

    const [values,setValues]=useState({
        name:'',
        email:'',
        password:'',
        error:'',
        loading:false,
        message:true,
        showForm:true
    })
    const {name,email,password,error,loading,message,showForm}=values

    const handleSubmit=(e)=>{
        e.preventDefault()
        console.table({name,email,password,error,loading,message,showForm})
    }

    const handleChange=name=>e=>{
        e.preventDefault()
        setValues({ ...values,error:false,[name]:e.target.value})
    }

    const signupForm=()=>{
        return(
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input value={name} onChange={handleChange('name')} type="text" className="form-control" placeholder="Enter name"></input>
                </div>
                <div className="form-group">
                    <input value="email" onChange={handleChange('email')} type="email" className="form-control" placeholder="Enter email"></input>
                </div>
                <div className="form-group">
                    <input value="password" onChange={handleChange('password')} type="text" className="form-control" placeholder="Enter password"></input>
                </div>
                <div>
                    <button className="btn btn-primary">Signup</button>
                </div>
            </form>
        )
    }

    return(
        <React.Fragment>
            {signupForm()}
        </React.Fragment>
    )
}

export default SignupComponent
