import fetch from 'isomorphic-fetch'
import {API} from '../config'
const signup=(user)=>{
    return fetch(`${API}/signup`,{
        method:POST,
        headers:{
            Accept:'application/json',
            'Content-type':'application/json'
        },
        body:JSON.stringify(user)
    }).then((response)=>{
        return response.json()
    }).catch((err)=>{
        console.log(err)
    })
    
}
