import {useState,useEffect} from 'react'
import Router from 'next/router'
import {isAuth, getCookie} from '../actions/auth'
import Link from 'next/link'
import { create, getCategories, removeCategory } from '../../actions/category';

const Category=()=>{
    const [values,setValues]=useState({
        name:'',
        error:false,
        success:false,
        category:[],
        remove:''
    })

    const {name,error,success,category,remove}=values
    const  token=getCookie('token')

    useEffect(() => {
        loadCategories();
    }, [reload]);

    const loadCategories = () => {
        getCategories().then((result)=>{
            console.log(result)
            result.json().then((data)=> {
            if (data.error) {
                console.log(data.error);
            } else {
                setValues({ ...values, categories: data });
            }
        });
    })
    };

    const showCategories = () => {
        return categories.map((c, i) => {
            return (
                <button
                    onDoubleClick={() => deleteConfirm(c.slug)}
                    title="Double click to delete"
                    key={i}
                    className="btn btn-outline-primary mr-1 ml-1 mt-3"
                >
                    {c.name}
                </button>
            );
        });
    };

    const deleteConfirm = slug => {
        let answer = window.confirm('Are you sure you want to delete this category?');
        if (answer) {
            deleteCategory(slug);
        }
    };

    const deleteCategory = slug => {
        // console.log('delete', slug);
        removeCategory(slug, token).then((result)=>{
            console.log(result)
            result.json().then((data) => {
            if (data.error) {
                console.log(data.error);
            } else {
                setValues({ ...values, error: false, success: false, name: '', removed: !removed, reload: !reload });
            }
        });
    })
    };

    const clickSubmit=(e)=>{
        e.preventDefault()
        create({name},token).then((result)=>{
            console.log(result)
            result.json().then((data)=>{
            if(data.error)
            {
                setValues({ ...values,error:data.error,success:false})
            }
            else{
                setValues({...values,name:'',error:false,success:true})
            }
            })
        })
    }
    const handleChange=(e)=>{
        setValues(...values,{name:e.target.value,error:false,success:false,remove:''})
    }

    const showSuccess = () => {
        if (success) {
            return <p className="text-success">Category is created</p>;
        }
    };

    const showError = () => {
        if (error) {
            return <p className="text-danger">Category already exist</p>;
        }
    };

    const showRemoved = () => {
        if (removed) {
            return <p className="text-danger">Category is removed</p>;
        }
    };

    const mouseMoveHandler = e => {
        setValues({ ...values, error: false, success: false, removed: '' });
    };
    
    const newCategory=()=>{
        <form onSubmit={clickSubmit}>
            <div className="form-group">
                <label className="text-mutes">Name Of Category</label>
                <input type="text" className="form-control" onChange={handleChange} value={name}></input>
                <button type="submit" className="btn btn-primary">create</button>
            </div>
        </form>
    }

return( <React.Fragment>
     {showSuccess()}
            {showError()}
            {showRemoved()}
            <div onMouseMove={mouseMoveHandler}>
                {newCategoryFom()}
                {showCategories()}
            </div>
    </React.Fragment>)
}   

export default Category
