import {useState,useEffect} from 'react'
import Router from 'next/router'
import {Signin,authenticate, isAuth} from '../../actions/auth'
const SigninComponent=()=>{

    const [values,setValues]=useState({
        email:'',
        password:'',
        error:'',
        loading:false,
        message:'',
        showForm:true
    })
    const {email,password,error,loading,message,showForm}=values

    useEffect(()=>{
        isAuth() && Router.push('/')
    },[])

    const handleSubmit=(e)=>{
        e.preventDefault()
        //console.table({name,email,password,error,loading,message,showForm})
        setValues({ ...values, loading:true,error:false})
        const user={email,password}

        Signin(user).then((result)=>{
            console.log(result)
            result.json().then((data)=>{
            if(data.error)
            {
                setValues({ ...values,error:data.error})
            }
            else{
                authenticate(data,()=>{
                    if(isAuth() && isAuth().role==1)
                    {
                        Router.push('/admin')
                    }
                    else{
                        Router.push('/user')
                    }
                })
                
            }
            })
        })
    }

    const handleChange=name=>e=>{
        e.preventDefault()
        setValues({ ...values,error:false,[name]:e.target.value})
    }

    const showLoading=()=>(loading ? <div className="alert alert-info">Loading...</div> : '')
    const showError=()=>(error ? <div className="alert alert-danger">{error}</div> : '')
    const showMessage=()=>(message ? <div className="alert alert-info">{message}</div> : '')

    const signinForm=()=>{
        return(
            <form onSubmit={handleSubmit}>
                <h2 style>SIGN IN</h2>
                <div className="form-group">
                    <input value={email} onChange={handleChange('email')} type="email" className="form-control" placeholder="Enter email"></input>
                </div>
                <div className="form-group">
                    <input value={password} onChange={handleChange('password')} type="password" className="form-control" placeholder="Enter password"></input>
                </div>
                <div>
                    <button className="btn btn-primary">Signin</button>
                </div>
            </form>
        )
    }

    return(
        <div className="content">
        <React.Fragment>
            {showError()}
            {showLoading()}
            {showMessage()}
            {showForm && signinForm()}
        </React.Fragment>
        </div>
    )
}

export default SigninComponent
